package d3d11

type (
	handle uintptr
	// Windows
	HMODULE handle

	// D3D
	IDXGIAdapter handle
)

const (
	SDK_VERSION = 7
)

type DRIVER_TYPE handle

const (
	D3D_DRIVER_TYPE_UNKNOWN   DRIVER_TYPE = 0
	D3D_DRIVER_TYPE_HARDWARE  DRIVER_TYPE = D3D_DRIVER_TYPE_UNKNOWN + 1
	D3D_DRIVER_TYPE_REFERENCE DRIVER_TYPE = D3D_DRIVER_TYPE_HARDWARE + 1
	D3D_DRIVER_TYPE_NULL      DRIVER_TYPE = D3D_DRIVER_TYPE_REFERENCE + 1
	D3D_DRIVER_TYPE_SOFTWARE  DRIVER_TYPE = D3D_DRIVER_TYPE_NULL + 1
	D3D_DRIVER_TYPE_WARP      DRIVER_TYPE = D3D_DRIVER_TYPE_SOFTWARE + 1
)

type FEATURE_LEVEL handle

const (
	FEATURE_LEVEL_9_1  FEATURE_LEVEL = 0x9100
	FEATURE_LEVEL_9_2  FEATURE_LEVEL = 0x9200
	FEATURE_LEVEL_9_3  FEATURE_LEVEL = 0x9300
	FEATURE_LEVEL_10_0 FEATURE_LEVEL = 0xa000
	FEATURE_LEVEL_10_1 FEATURE_LEVEL = 0xa100
	FEATURE_LEVEL_11_0 FEATURE_LEVEL = 0xb000
)

const (
	CREATE_DEVICE_SINGLETHREADED                           = 0x1
	CREATE_DEVICE_DEBUG                                    = 0x2
	CREATE_DEVICE_SWITCH_TO_REF                            = 0x4
	CREATE_DEVICE_PREVENT_INTERNAL_THREADING_OPTIMIZATIONS = 0x8
	CREATE_DEVICE_BGRA_SUPPORT                             = 0x20
)
